<template>
    <div class="public">
        <mt-tabbar v-model="selected">
          <mt-tab-item id="1">
            <img slot="icon" v-if="pathname!='/'" :src="pic1" style="    position: relative;
    left: 0.02rem;">
            <img slot="icon" v-if="pathname=='/'" :src="pic2" style="    position: relative;
    left: 0.02rem;">
            <div :style="{color:pathname=='/'?'#222':'#999'}" style="position: relative;top: 0.05rem;position: absolute;text-align: center;left:0;right:0;">首页</div>
          </mt-tab-item>
          <mt-tab-item id="2" v-if="ifopen==1 || localopen==1">
            <img slot="icon" :src="pic3" style="width:0.6rem;height:0.6rem;top: -0.08rem;position: relative;left: -0.04rem;">
            <div :style="{color:pathname=='/collegeHome'?'#222':'#999'}" style="position: relative;top: 0.05rem;position: absolute;text-align: center;left:0;right:0;">摄影学院</div>
          </mt-tab-item>
          <mt-tab-item id="3">
            <img slot="icon" v-if="pathname!='/study'" :src="pic5">
            <img slot="icon" v-if="pathname=='/study'" :src="pic6">
            <div :style="{color:pathname=='/study'?'#222':'#999'}" style="position: relative;top: 0.05rem;position: absolute;text-align: center;left:0;right:0;">学习</div>
          </mt-tab-item>
          <mt-tab-item id="4">
            <img slot="icon" v-if="pathname!='/mine'" :src="pic7">
            <img slot="icon" v-if="pathname=='/mine'" :src="pic8">
            <div :style="{color:pathname=='/mine'?'#222':'#999'}" style="position: relative;top: 0.05rem;position: absolute;text-align: center;left:0;right:0;">我的</div>
          </mt-tab-item>
        </mt-tabbar>
    </div>
</template>
<script>
    import { Tabbar, TabItem } from 'mint-ui';
    export default{
        props:['ifopen'],
        data(){
            return {
                localopen:0,
                selected:1,
                pathname:'',
                pic1:'data:image/png;base64,',
                pic2:'data:image/png;base64,',
                pic3:'data:image/png;base64,',
                pic4:'',
                pic5:'data:image/png;base64,',
                pic6:'data:image/png;base64,',
                pic7:'data:image/png;base64,',
                pic8:'data:image/png;base64,',
            }
        },
        created(){
            this.pathname = window.location.pathname;
            if(localStorage.getItem('ifopen') == 0){
              this.localopen = 0;
            }else{
              this.localopen = 1;
            }
            console.log(this.localopen)
        },
        watch:{
            selected(val,oldval){
                var self = this;
                if(this.$route.query.ifopen != undefined){
                  this.ifopen = this.$route.query.ifopen
                }
                if(val == 1){
                    self.$router.push({path:'/'});
                }else if(val == 2){
                    self.$router.push({path:'/collegeHome'});
                }else if(val == 3){
                    self.$router.push({path:'/study'});
                }else if(val == 4){
                    self.$router.push({path:'/mine'});
                }
            }
        }
    }
</script>
<style scoped lang="less">
    .public{

    }
</style>